import React, { useState } from "react";
import "./App.css";

const initialFacultyData = [
  {
    id: 1,
    name: "Dr. Alice Johnson",
    department: "Computer Science",
    committees: ["Curriculum Committee", "Technology Committee"],
  },
  {
    id: 2,
    name: "Dr. Bob Smith",
    department: "Mathematics",
    committees: ["Budget Committee", "Hiring Committee"],
  },
  {
    id: 3,
    name: "Dr. Carol Lee",
    department: "Physics",
    committees: ["Research Committee"],
  },
];

function FacultyList() {
  const [facultyData, setFacultyData] = useState(initialFacultyData);
  const [expanded, setExpanded] = useState({});
  const [newFaculty, setNewFaculty] = useState({ name: "", department: "", committees: "" });
  const [editingFaculty, setEditingFaculty] = useState(null);

  const toggleExpand = (id) => {
    setExpanded((prev) => ({ ...prev, [id]: !prev[id] }));
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewFaculty((prev) => ({ ...prev, [name]: value }));
  };

  const addFaculty = () => {
    if (!newFaculty.name || !newFaculty.department) return;
    setFacultyData((prev) => [
      ...prev,
      {
        id: prev.length + 1,
        name: newFaculty.name,
        department: newFaculty.department,
        committees: newFaculty.committees.split(",").map((c) => c.trim()),
      },
    ]);
    setNewFaculty({ name: "", department: "", committees: "" });
  };

  const startEditing = (faculty) => {
    setEditingFaculty(faculty);
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditingFaculty((prev) => ({ ...prev, [name]: value }));
  };

  const saveEdit = () => {
    setFacultyData((prev) =>
      prev.map((faculty) =>
        faculty.id === editingFaculty.id
          ? { ...editingFaculty, committees: editingFaculty.committees.split(",").map((c) => c.trim()) }
          : faculty
      )
    );
    setEditingFaculty(null);
  };

  return (
    <div className="faculty-list">
      <h1>College Faculty & Committees</h1>
      {facultyData.map((faculty) => (
        <div key={faculty.id} className="faculty-item">
          <button onClick={() => toggleExpand(faculty.id)}>
            {expanded[faculty.id] ? "▼" : "▶"} {faculty.name}
          </button>
          {expanded[faculty.id] && (
            <div className="faculty-details">
              {editingFaculty?.id === faculty.id ? (
                <div className="edit-form">
                  <input
                    type="text"
                    name="name"
                    value={editingFaculty.name}
                    onChange={handleEditChange}
                  />
                  <input
                    type="text"
                    name="department"
                    value={editingFaculty.department}
                    onChange={handleEditChange}
                  />
                  <input
                    type="text"
                    name="committees"
                    value={editingFaculty.committees}
                    onChange={handleEditChange}
                  />
                  <button onClick={saveEdit}>Save</button>
                </div>
              ) : (
                <>
                  <p><strong>Department:</strong> {faculty.department}</p>
                  <p><strong>Committees:</strong></p>
                  <ul>
                    {faculty.committees.map((committee, index) => (
                      <li key={index}>{committee}</li>
                    ))}
                  </ul>
                  <button onClick={() => startEditing(faculty)}>Edit</button>
                </>
              )}
            </div>
          )}
        </div>
      ))}

      <div className="add-faculty">
        <h2>Add New Faculty</h2>
        <input
          type="text"
          name="name"
          placeholder="Name"
          value={newFaculty.name}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="department"
          placeholder="Department"
          value={newFaculty.department}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="committees"
          placeholder="Committees (comma-separated)"
          value={newFaculty.committees}
          onChange={handleInputChange}
        />
        <button onClick={addFaculty}>Add Faculty</button>
      </div>
    </div>
  );
}

export default function App() {
  return (
    <div className="App">
      <FacultyList />
    </div>
  );
}
